import React, { useState,useEffect } from "react";
import { useLocation, useNavigate } from 'react-router-dom';

export default function EditProduct() {

  const location = useLocation();
  const initialProduct = location.state && location.state.product ? location.state.product : {};
  const [product, setProduct] = useState(initialProduct);

  console.log("product id", product);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setProduct({ ...product, [name]: value });
  };
  const handleKaydet = () => {
    fetch("http://example.com/kaydet", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(product),
    })
      .then((response) => response.json())
      .then((data) => console.log(data))
      .catch((error) => console.error(error));
  };



  const fetchProduct = async (id) => {
    try {
      const response = await fetch(`https://api.monjardin.online/api/Product/GetProductDetailByProductId/${id}`);
      const data = await response.json();
      console.log("response",response)
      console.log("data", data)
      setProduct(data);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    // productId'yi kullanarak ilgili ürünü getirin ve setProduct ile state'i güncelleyin
    console.log("id",product)
    fetchProduct(product.id);
  }, [product.id]);

  const navigate = useNavigate();

  const handleGoBack = () => {
    navigate(-1); // Bir önceki sayfaya yönlendirir
  };
  return(
    <div style={{ marginTop:"100px", paddingLeft:"50px", paddingRight:"50px" }}>
        <button onClick={handleGoBack} className='back-button'>Geri</button>
         {product && (
        <>
        <div style={{display:"flex"}}>
            <div >
              <img src={product.image} alt="Resim 1" width={250} height={320}/> 
            </div>
            <div style={{ display:"block",marginLeft: "20px" }}>
            <div style={{padding:"20px"}}> 
              <div style={{fontStyle:"italic",fontFamily:"Times New Roman"}}>Stok No:</div>
            <input
              type="text"
              name="stokNo"
              value={product.stockNo}
              onChange={handleInputChange}
              className="edit-input-area"
              />

              </div>
            <div style={{padding:"20px"}}>      
              <div style={{fontStyle:"italic",fontFamily:"Times New Roman"}}>KDV Oranı:</div>
            <input
              type="text"
              name="kdvOrani"
              value={product.taxRate}
              onChange={handleInputChange}
              className="edit-input-area"
            /></div>
            <div style={{padding:"20px"}}>
            <div style={{fontStyle:"italic",fontFamily:"Times New Roman"}}>Ürün Rengi:</div>
            <input
              type="text"
              name="urunRengi"
              value={product.color}
              onChange={handleInputChange}
              className="edit-input-area"
            /></div>
            </div>
            <div style={{ display:"block", marginLeft: "20px" }}>
              <div style={{padding:"20px"}}> 
              <div style={{fontStyle:"italic",fontFamily:"Times New Roman"}}>Ürün Adı:</div>
              <input
                type="text"
                name="urunAdi"
                value={product.name}
                onChange={handleInputChange}
                className="edit-input-area"
              />
              </div>
              <div style={{padding:"20px"}}> 
              <div style={{fontStyle:"italic",fontFamily:"Times New Roman"}}>Ürün Adedi:</div>
              <input
                type="number"
                name="urunAdedi"
                value={product.quantity}
                onChange={handleInputChange}
                className="edit-input-area"
              />
               </div>
              <div style={{padding:"20px"}}> 
              <div style={{fontStyle:"italic",fontFamily:"Times New Roman"}}>Saksı:</div>
              <input
                type="text"
                name="saksi"
                value={product.saksi}
                onChange={handleInputChange}
                className="edit-input-area"
              />
               </div>
            </div>
            <div style={{ display:"block",marginLeft: "20px" }}>
            <div style={{padding:"20px"}}> 
              <div style={{fontStyle:"italic",fontFamily:"Times New Roman"}}>Barkod:</div>
              <input
                type="text"
                name="barkod"
                value={product.barcode}
                onChange={handleInputChange}
                className="edit-input-area"
              />
               </div>
              <div style={{padding:"20px"}}> 
              <div style={{fontStyle:"italic",fontFamily:"Times New Roman"}}>Fiyat:</div>
              <input
                type="text"
                name="fiyat"
                value={product.price}
                onChange={handleInputChange}
                className="edit-input-area"
              />
               </div>
              <div style={{padding:"20px"}}> 
              <div style={{fontStyle:"italic",fontFamily:"Times New Roman"}}>İndirim:</div>
              <input
                type="text"
                name="fiyat"
                value={product.discount}
                onChange={handleInputChange}
                className="edit-input-area"
              />
               </div>

            </div>


        </div>
        <div style={{ display:"block",marginTop: "50px"}}>
            <div style={{fontStyle:"italic",fontFamily:"Times New Roman"}}>Açıklaması:</div>
              <textarea
                type="textarea"
                name="fiyat"
                value={product.description}
                onChange={handleInputChange}
                style={{ padding:"10px", width:"100%" }}
              />
            </div>

            <button onClick={handleKaydet} className="back-button" style={{float:"right", marginTop:"30px"}}>Kaydet</button>

        </>
    )}
  </div>



  );


}
