        price: 800.99,
        taxRate: 18,
        description: " Zarafetin sembolü olan orkide çiçeği narin ve güzel bir çiçektir",
        discount: 0.1,
        barcode: "123456789",
      },
      {
        id: 2,
        name: "Pembe Lale Buketi",
        image: "/images/orkide.png",
        stockNo: "02",
        quantity: 20,
        price: 2999.99,
        taxRate: 18,
        description: "Her lale renginin bir anlamı vardır",
