import React, { useState , useEffect} from "react";
import Admincard from './Admincard'; // Card bileşenini import edin
import { useNavigate } from 'react-router-dom';

const AdminSearch= () => {
  const data = [
    { id: 1, title: 'Kart 1', content: 'İçerik 1', image: '/images/kart1.png'},
    { id: 2, title: 'Kart 2', content: 'İçerik 2', image: '/images/kart2.png' },
    { id: 3, title: 'Kart 3', content: 'İçerik 3', image: '/images/kart3.png' },
    { id: 4, title: 'Kart 4', content: 'İçerik 4', image: '/images/kart4.png' },
    { id: 5, title: 'Kart 5', content: 'İçerik 5', image: '/images/kart5.png' },
    { id: 6, title: 'Kart 6', content: 'İçerik 6', image: '/images/kart6.png' },
    // Diğer kartlar buraya eklenebilir
  ];
  const [previousSearches, setPreviousSearches] = useState([]);
  const [categoryList, setCategoryList] = useState([]);

  const search = (event) => {
    event.preventDefault();
    const searchTerm = event.target.elements.searchTerm.value;
    if (searchTerm) {
      setPreviousSearches([...previousSearches, searchTerm]);
      event.target.elements.searchTerm.value = "";
    }
  };
  const navigate = useNavigate();

  const handleGoBack = () => {
    navigate(-1); // Bir önceki sayfaya yönlendirir
  };

  const fetchCategoryList = async () => {
    try {

      const response = await fetch(`https://api.monjardin.online/api/Category/GetMainCategories`);
      const data = await response.json();
      console.log("data",data.data)
      const categoryData= data.data;
      setCategoryList(categoryData);  
      //setFilteredProducts(categoryData);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchCategoryList();
  }, []);

  return (
    <div style={{ margin: "100px" }}>
       <h1  className='baslik'>Mon Jardin</h1>

      <button onClick={handleGoBack} className='back-button'><img src="/images/back-button.png" alt="" width={40} height={30}/></button>

      <div className="search-area">
          <form onSubmit={search}> 
          <input
            type="text"
            name="searchTerm"
            placeholder="Arama yapmak için buraya yazın..."
            className="admin-search-area"
          />
            <button type="submit" className='search-button'>
            <img src="/images/search.png" alt="" width={32} height={32} />
          </button>

          <Admincard />
          </form>
        </div>

    </div>  
  );
}

export default AdminSearch;
