import React, { useState } from "react";
import { useLocation, useNavigate } from 'react-router-dom';

export default function EditProduct() {
  const location = useLocation();
const navigate = useNavigate();

  const product = location.state.product;

// Şimdi product verilerini kullanarak düzenleme sayfasını oluşturabilirsiniz
// Örneğin, ürünün tüm bilgilerini yazdırmak için:
console.log(product);
  const [urun, setUrun] = useState({
    resimler: ["resim1.png"],
    stokNo: "123456",
    kdvOrani: "18%",
    urunRengi: "Mavi",
    urunAdi: "Örnek Ürün",
    urunAdedi: 10,
    saksi: "Var",
    barkod: "7891011121314",
    fiyat: "50 TL",
  });
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setUrun({ ...urun, [name]: value });
  };
  const handleKaydet = () => {
    fetch("http://example.com/kaydet", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(urun),
    })
      .then((response) => response.json())
      .then((data) => console.log(data))
      .catch((error) => console.error(error));
  };
  return(
    <div style={{ marginTop:"100px", display: "flex" }}>
    <div>
      <img src={product.image} alt="Resim 1" />

    </div>
    <div style={{ display:"block",marginLeft: "20px" }}>
     <div> <p>Stok No:</p>
     <input
       type="text"
       name="stokNo"
       value={product.stokNo}
       onChange={handleInputChange}
     /></div>
     <div>      <p>KDV Oranı:</p>
     <input
       type="text"
       name="kdvOrani"
       value={product.taxRate}
       onChange={handleInputChange}
     /></div>
     <div>
     <p>Ürün Rengi:</p>
     <input
       type="text"
       name="urunRengi"
       value={product.color}
       onChange={handleInputChange}
     /></div>
     </div>

      <div style={{ marginLeft: "20px" }}>
      <p>Ürün Adı:</p>
      <input
        type="text"
        name="urunAdi"
        value={product.name}
        onChange={handleInputChange}
      />
      <p>Ürün Adedi:</p>
      <input
        type="number"
        name="urunAdedi"
        value={product.quantity}
        onChange={handleInputChange}
      />
      <p>Saksı:</p>
      <input
        type="text"
        name="saksi"
        value={product.saksi}
        onChange={handleInputChange}
      />
    </div>

    <div style={{ marginLeft: "20px" }}>
      <p>Barkod:</p>
      <input
        type="text"
        name="barkod"
        value={product.barcode}
        onChange={handleInputChange}
      />
      <p>Fiyat:</p>
      <input
        type="text"
        name="fiyat"
        value={product.price}
        onChange={handleInputChange}
      />
    </div>

    <button onClick={handleKaydet}>Kaydet</button>
  </div>



  );


}
