import React, { useState,useEffect } from "react";
import { useLocation, useNavigate } from 'react-router-dom';

export default function EditProduct() {
  const location = useLocation();
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setProduct({ ...product, [name]: value });
  };
  const handleKaydet = () => {
    fetch("http://example.com/kaydet", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(product),
    })
      .then((response) => response.json())
      .then((data) => console.log(data))
      .catch((error) => console.error(error));
  };

  const [product, setProduct] = useState(location.state.product);
console.log(product)
  const fetchProduct = async (id) => {
    try {
      const response = await fetch(`https://api.monjardin.online/api/Product/GetProductDetailByProductId/${id}`);
      const data = await response.json();
      console.log("response",response)
      console.log("data", data)
      setProduct(data);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    // productId'yi kullanarak ilgili ürünü getirin ve setProduct ile state'i güncelleyin
    fetchProduct(product.id);
  }, [product.id]);

  return(
    <div style={{ marginTop:"100px", display: "flex", padding:"50px" }}>
         {product && (
        <>

            <div>
              <img src={product.image} alt="Resim 1" width={250} height={320}/> 
            </div>
            <div style={{ display:"block",marginLeft: "20px" }}>
            <div> 
              <p>Stok No:</p>
            <input
              type="text"
              name="stokNo"
              value={product.stockNo}
              onChange={handleInputChange}
            /></div>
            <div>      
              <p>KDV Oranı:</p>
            <input
              type="text"
              name="kdvOrani"
              value={product.taxRate}
              onChange={handleInputChange}
            /></div>
            <div>
            <p>Ürün Rengi:</p>
            <input
              type="text"
              name="urunRengi"
              value={product.color}
              onChange={handleInputChange}
            /></div>
            </div>
            <div style={{ marginLeft: "20px" }}>
              <p>Ürün Adı:</p>
              <input
                type="text"
                name="urunAdi"
                value={product.name}
                onChange={handleInputChange}
              />
              <p>Ürün Adedi:</p>
              <input
                type="number"
                name="urunAdedi"
                value={product.quantity}
                onChange={handleInputChange}
              />
              <p>Saksı:</p>
              <input
                type="text"
                name="saksi"
                value={product.saksi}
                onChange={handleInputChange}
              />
            </div>
            <div style={{ marginLeft: "20px" }}>
              <p>Barkod:</p>
              <input
                type="text"
                name="barkod"
                value={product.barcode}
                onChange={handleInputChange}
              />
              <p>Fiyat:</p>
              <input
                type="text"
                name="fiyat"
                value={product.price}
                onChange={handleInputChange}
              />
              <p>Açıklaması:</p>
              <input
                type="textarea"
                name="fiyat"
                value={product.description}
                onChange={handleInputChange}
              />
                <p>İndirim:</p>
              <input
                type="text"
                name="fiyat"
                value={product.discount}
                onChange={handleInputChange}
              />
            </div>
            <button onClick={handleKaydet} className="back-button">Kaydet</button>

        </>
    )}
  </div>



  );


}
